mapper.select:
select t.channel_id_
from ujcms_group_access t
where t.group_id_ = #{groupId}
and t.site_id_ = #{siteId}

select t.group_id_
from ujcms_group_access t
where t.channel_id_ = #{channelId}
and t.site_id_ = #{siteId}

mapper.delete:
delete
from ujcms_group_access
where group_id_ = #{groupId,jdbcType=INTEGER}
and site_id_ = #{siteId}

mapper.select:
select 

from ujcms_group_access t
where t.group_id_ = #{groupId,jdbcType=INTEGER}
  and t.channel_id_ = #{channelId,jdbcType=INTEGER}

mapper.delete:
delete from ujcms_group_access
where group_id_ = #{groupId,jdbcType=INTEGER}
  and channel_id_ = #{channelId,jdbcType=INTEGER}

mapper.update:
update ujcms_group_access
set site_id_ = #{siteId,jdbcType=INTEGER}
where group_id_ = #{groupId,jdbcType=INTEGER}
  and channel_id_ = #{channelId,jdbcType=INTEGER}
