mapper.select:
select

from ujcms_site t
where parent_id_ = #{parentId,jdbcType=INTEGER}

select

from ujcms_site t
where t.sub_dir_ = #{subDir}

mapper.update:
update ujcms_site
set depth_ = depth_ + #{depth,jdbcType=SMALLINT}
where id_ in (select descendant_id_ from ujcms_site_tree where ancestor_id_ = #{id,jdbcType=INTEGER})

update ujcms_site
set order_ = order_ + #{num,jdbcType=INTEGER}
where order_ >= #{begin,jdbcType=INTEGER}
  and order_ <= #{end,jdbcType=INTEGER}

update ujcms_site
set order_ = order_ + #{num,jdbcType=INTEGER}
where exists(select * from ujcms_site_tree t where t.ancestor_id_ = #{parentId,jdbcType=INTEGER} and t.descendant_id_ = id_)

mapper.select:
select

from ujcms_site t
where t.id_ = #{id,jdbcType=INTEGER}
